import React, { useState } from "react";
import {
  PlasmicHome,
  DefaultHomeProps,
} from "component/plasmic/home/PlasmicHome";
import Update from "component/update";
import {
  options,
  initialValueEmpty,
} from "component/editor/config/initialValues";
import Searcher from "@venturemark/numnum";
import { Node } from "slate";
import { format } from "date-fns";
import { useForm } from "react-hook-form";
import { serialize } from "module/serialize";
import { get } from "module/store";
import * as linechart from "component/linechart";
import { useEditor } from "component/editor/compose";
import { UpdateType } from "component/update";
import TimelineItem, { TimelineItemType } from "component/timelineitem";

interface HomeProps extends DefaultHomeProps {}

const defaultTimelines: TimelineItemType[] = [
  {
    name: "Revenue",
    id: "now",
    date: "now",
  },
  {
    name: "Active Users",
    id: "now",
    date: "now",
  },
  {
    name: "Features Shipped",
    id: "now",
    date: "now",
  },
  {
    name: "Milestones",
    id: "now",
    date: "now",
  },
];

const defaultText: Node[] = [
  {
    children: [
      {
        type: options.p.type,
        children: [
          {
            text: "Be Better Tomorrow",
          },
        ],
      },
    ],
  },
];

const defaultUpdates: UpdateType[] = [
  {
    id: "now",
    numberValue: 23,
    flipped: false,
    text: defaultText,
  },
];

const defaultData = [
  {
    date: "January 1, 2019",
    cac: 50,
  },
  {
    date: "February 2, 2019",
    cac: 55,
  },
  {
    date: "March 3, 2019",
    cac: 40,
  },
  {
    date: "April 1, 2019",
    cac: 35,
  },
  {
    date: "May 10, 2019",
    cac: 39,
  },
  {
    date: "June 1, 2019",
    cac: 40,
  },
  {
    date: "July 1, 2019",
    cac: 50,
  },
];

const dataKey = "cac";
const name = "Customer Acquisition Cost";

type FormInputs = {
  name: string;
};

export function Component(props: HomeProps) {
  const [updates, setUpdates] = useState<UpdateType[]>(defaultUpdates);
  const [metrics, setMetrics] = useState<linechart.DataItem[]>(defaultData);
  const [timelines, setTimelines] = useState<TimelineItemType[]>(
    defaultTimelines
  );
  const [showSidebar, setShowSidebar] = useState(true);
  const { register, handleSubmit, reset } = useForm<FormInputs>();

  const store = get("composeEditor.content") ?? "";
  const initialValue = store !== "" ? JSON.parse(store) : initialValueEmpty;
  const hasContentDefault =
    serialize(initialValue) === "" || serialize(initialValue) === undefined
      ? undefined
      : "hasContent";
  const defaultNumber = Searcher.Search(serialize(initialValue))
    ? Searcher.Search(serialize(initialValue))[0]
    : undefined;
  const defaultProgress = serialize(initialValue).length;

  const { editorShape, setEditorShape } = useEditor({
    value: initialValue,
    hasContent: hasContentDefault,
    numberValue: defaultNumber,
    progress: defaultProgress,
  });

  const createUpdate = () => {
    if (!editorShape.hasContent) {
      console.log("content text");
      const error = "Please enter some text";
      setEditorShape({ ...editorShape, error });
      return;
    }

    if (!editorShape.numberValue) {
      const error = "Please enter a number";
      setEditorShape({ ...editorShape, error });
      return;
    }

    if (serialize(editorShape.value).length > 241) {
      const error = `Your update is ${
        serialize(editorShape.value).length
      } characters. The limit is 240 characters`;
      setEditorShape({ ...editorShape, error });
      return;
    }

    const id = new Date();

    const update = {
      text: editorShape.value,
      numberValue: editorShape.numberValue,
      id: id.toString(),
      flipped: false,
    };
    setUpdates([update, ...updates]);

    const metric = {
      date: format(new Date(), "PP"),
      cac: editorShape.numberValue,
    };
    setMetrics([...metrics, metric]);

    //reset store
    localStorage.setItem(
      "composeEditor.content",
      JSON.stringify(initialValueEmpty)
    );
    //reset editor
    const resetEditor = {
      value: initialValueEmpty,
      string: "",
      hasContent: undefined,
      numberValue: undefined,
      error: undefined,
      progress: 0,
    };
    setEditorShape(resetEditor);
  };

  const handleAddTimeline = (data: FormInputs, e: any) => {
    if (!data.name) {
      return;
    }

    const timeline = {
      name: data.name,
      date: format(new Date(), "PP"),
      id: format(new Date(), "PP"),
    };
    setTimelines([timeline, ...timelines]);

    //reset form
    reset({
      name: "",
    });
  };

  return (
    <PlasmicHome
      sidebarHidden={showSidebar}
      homeButton={{
        "aria-label": "Toggle sidebar",
        onPress: () => {
          setShowSidebar(!showSidebar);
        },
      }}
      timelineButton={{
        "aria-label": "Toggle sidebar",
        onPress: () => handleSubmit(handleAddTimeline)(),
      }}
      addTimeline={{
        onSubmit: handleSubmit(handleAddTimeline),
      }}
      addTimelineInput={{
        name: "name",
        ref: register,
      }}
      timelinesContainer={{
        children: timelines.map((timeline) => (
          <TimelineItem name={timeline.name} />
        )),
      }}
      actionBar={{
        errorMessage: editorShape.error,
        progress: editorShape.progress,
        editorShape: editorShape,
        setEditorShape: setEditorShape,
      }}
      updatesContainer={{
        children: updates.map((update) => (
          <Update
            text={update.text}
            key={update.id}
            id={update.id}
            dataKey={dataKey}
            data={metrics}
            name={name}
            flipped={update.flipped}
            updates={updates}
            setUpdates={setUpdates}
          />
        )),
      }}
      actionsColumn={{
        hasContent: editorShape.hasContent,
        numberValue: editorShape.numberValue,
        createUpdate: createUpdate,
      }}
    />
  );
}
