import React, { useContext } from "react";
import {
  PlasmicMain,
  DefaultMainProps,
} from "component/plasmic/shared/PlasmicMain";
import { TimelineContext, VentureContext } from "component/app";
import { useForm } from "react-hook-form";
import { IUser } from "module/interface/user";
import { useParams } from "react-router-dom";
import { ITimeline } from "module/interface/timeline";

interface MainProps extends DefaultMainProps {
  isOnboarding?: boolean | "isOnboarding";
  isActive: any;
  variantType: any;
  setIsVisible: any;
  setPost: any;
  viewCreateVenture?: any;
  viewJoinVenture?: any;
  ventureId?: string;
  user: IUser;
}

function Main(props: MainProps) {
  const {
    isOnboarding,
    isActive,
    variantType,
    setIsVisible,
    setPost,
    viewCreateVenture,
    viewJoinVenture,
    ventureId,
    user,
    ...rest
  } = props;
  const { timelineSlug } = useParams();

  const timelineContext = useContext(TimelineContext);
  const ventureContext = useContext(VentureContext);
  const currentVenture = ventureContext?.currentVenture;
  const timelines = timelineContext?.timelines ?? [];

  const { handleSubmit, register, reset, watch, errors } = useForm();
  const watchData = watch();

  const currentTimeline = timelineSlug
    ? timelines?.filter((timeline: ITimeline) => {
        return (
          timeline.name.toLowerCase().replace(/\s/g, "") ===
            timelineSlug?.toLowerCase().replace(/\s/g, "") ?? ""
        );
      })[0]
    : undefined;

  return (
    <PlasmicMain
      {...rest}
      isActive={isActive}
      variantType={variantType}
      mainHeader={{
        isActive,
        variantType,
        currentTimeline,
        watchData,
        isOnboarding,
        currentVenture,
      }}
      feedUpdate={{
        setIsVisible,
        timelines,
        currentTimeline,
        currentVenture,
        setPost,
        user,
      }}
      addEditMembers={{
        currentVenture,
        currentTimeline,
        user,
      }}
      addEditVenture={{
        currentVenture,
        handleSubmit,
        register,
        reset,
        errors,
      }}
      addEditTimeline={{
        currentVenture,
        currentTimeline,
        setIsVisible,
        handleSubmit,
        register,
        reset,
        errors,
      }}
      viewCreateVenture={{
        onPress: () => viewCreateVenture(),
      }}
      viewJoinVenture={{ onPress: () => viewJoinVenture }}
    />
  );
}

export default Main;
