import * as React from "react";
import {
  PlasmicProfileForm,
  DefaultProfileFormProps,
} from "component/plasmic/shared/PlasmicProfileForm";
import { useAuth0 } from "@auth0/auth0-react";
import { useForm } from "react-hook-form";
import { useHistory } from "react-router-dom";
import { saveUser, getUser } from "module/store";
import { INewUser } from "module/interface/user";
import { nameError, roleError } from "module/errors";

interface ProfileFormProps extends DefaultProfileFormProps {
  isVisible?: any;
  setIsVisible?: any;
}

function ProfileForm(props: ProfileFormProps) {
  const { isVisible, setIsVisible, ...rest } = props;
  const history = useHistory();
  const { user: authUser } = useAuth0();
  const user = getUser() ? getUser() : authUser;
  const { handleSubmit, register, errors } = useForm();

  const handleSave = (data: any) => {
    if (!data.name) {
      return;
    }

    const user: INewUser = {
      id: data.name.toLowerCase().replace(/\s/g, ""),
      name: data.name,
      title: data.title,
      token: "someToken",
    };

    saveUser(user);

    history.push("/");
  };

  return (
    <PlasmicProfileForm
      {...rest}
      form={{
        onSubmit: handleSubmit(handleSave),
      }}
      nameField={{
        name: "name",
        defaultValue: user?.name ?? "",
        register: register({ required: true }),
        errorMessage: errors.name && nameError,
      }}
      jobField={{
        name: "title",
        defaultValue: user?.title ?? "",
        register: register({ required: true }),
        errorMessage: errors.role && roleError,
      }}
      save={{
        type: "submit",
        onClick: () => setIsVisible(undefined),
      }}
      cancel={{
        onClick: () => setIsVisible(undefined),
      }}
      close={{
        onClick: () => setIsVisible(undefined),
      }}
    />
  );
}

export default ProfileForm;
