import * as React from "react";
import {
  PlasmicProfileForm,
  DefaultProfileFormProps,
} from "component/plasmic/shared/PlasmicProfileForm";
import { useAuth0 } from "@auth0/auth0-react";
import { useForm } from "react-hook-form";
import { useHistory } from "react-router-dom";

interface ProfileFormProps extends DefaultProfileFormProps {}

function ProfileForm(props: ProfileFormProps) {
  const history = useHistory();
  const { user } = useAuth0();
  const { handleSubmit, register } = useForm();

  const handleComplete = (data: any) => {
    //currently we're not storing a change here because we're waiting on backend.
    console.log(data);
    history.push("/");
  };

  return (
    <PlasmicProfileForm
      {...props}
      form={{
        onSubmit: handleSubmit(handleComplete),
      }}
      nameField={{
        defaultValue: user?.name ?? "",
        register: register(),
        name: "name",
      }}
      jobField={{
        register: register(),
        name: "job",
      }}
      completeProfile={{
        type: "submit",
      }}
    />
  );
}

export default ProfileForm;
