import React, { useRef } from 'react';
import { PressEvent } from '@react-types/shared';
import {
  PlasmicSidebarItem,
  DefaultSidebarItemProps,
} from 'component/plasmic/shared/PlasmicSidebarItem';
import { useForm } from 'react-hook-form';
import {
  INewTimeline,
} from 'module/interface/timeline';
import {
  useCreateTimeline,
} from 'module/hook/timeline';

interface SidebarItemProps extends DefaultSidebarItemProps {
  name: string;
  userId: string;
  organizationId: string;
  onClick?: (e: React.MouseEvent) => void;
  onPress?: (e: PressEvent) => void;
  isCurrent: boolean;
  isInput?: boolean;
}

type FormInputs = {
  name: string;
};

function SidebarItem(props: SidebarItemProps) {
  const {
    name,
    userId,
    organizationId,
    onClick,
    isCurrent,
    isInput,
  } = props;

  const { register, handleSubmit, reset } = useForm<FormInputs>();

  const { mutate: createTimeline } = useCreateTimeline();
  const nameRef = useRef<HTMLInputElement | null>(null);

  const handleAddTimeline = (data: FormInputs) => {
    if (!data.name) {
      return;
    }

    const newTimeline: INewTimeline = {
      name: data.name,
      desc: 'edit timeline description',
      userId,
      audienceId: '1',
      organizationId,
    };

    // audienceMutation(timelineId)
    createTimeline(newTimeline);

    //reset form
    reset({
      name: '',
    });
  };



  return (
    <PlasmicSidebarItem
      isInput={isInput}
      name={name}
      isCurrent={isCurrent}
      onClick={onClick}
      sidebarForm={{
        onSubmit: handleSubmit(handleAddTimeline),
      }}
      addTimelineInput={{
        name: 'name',
        ref: (e) => {
          register(e);
          nameRef.current = e;
        },
      }}
    />
  );
}

export default SidebarItem;
